<?php$this->pageTitle = Yii::app()->name . ' - ' . UserModule::t("Login");$this->breadcrumbs = array(    UserModule::t("Login"),);?><div class="row">    <div class="span7" style="text-align:center">	<?php echo CHtml::image(Yii::app()->request->baseUrl . '/images/map.jpg'); ?>    </div>    <div class="span5">        <div style="text-align:center"><h3># 会员登录 #</h3></div>        <div>	    <?php if (Yii::app()->user->hasFlash('loginMessage')): ?>    	    <div class="success">		    <?php //echo Yii::app()->user->getFlash('loginMessage'); ?>		    <?php		    $this->widget('bootstrap.widgets.TbAlert', array(			'block' => true, // display a larger alert block?			'fade' => true, // use transitions?			'closeText' => '&times;', // close link text - if set to false, no close link is displayed			'alerts' => array(// configurations per alert type			    'loginMessage' => array('block' => true, 'fade' => true, 'closeText' => '&times;'), // success, info, warning, error or danger			),		    ));		    ?>    	    </div>	    <?php endif; ?>            <p class="help-block" style="text-align:center"><?php echo UserModule::t("Please fill out the following form with your login credentials:"); ?></p>            <div class="form">		<?php		$form = $this->beginWidget('bootstrap.widgets.TbActiveForm', array(		    'id' => 'login-form',		    'layout' => TbHtml::FORM_LAYOUT_HORIZONTAL,		    'helpType' => TbHtml::HELP_TYPE_BLOCK,		    'enableClientValidation' => true,		    'clientOptions' => array(			'validateOnSubmit' => true,		    ),		));		?>		<?php //echo $form->errorSummary($model); ?>		<?php echo $form->textFieldControlGroup($model, 'username'); ?>		<?php echo $form->passwordFieldControlGroup($model, 'password'); ?>		<div class="control-group">		    <div class="controls">			<?php echo TbHtml::link(UserModule::t("Register"), Yii::app()->getModule('user')->registrationUrl); ?> | <?php echo TbHtml::link(UserModule::t("Lost Password?"), Yii::app()->getModule('user')->recoveryUrl); ?>		    </div>		</div>		<?php echo $form->checkBoxControlGroup($model, 'rememberMe'); ?>		<div class="control-group">		    <div class="controls">			<?php echo TbHtml::submitButton('Login', array('class'=>'btn btn-primary')); ?>		    </div>		</div>		<?php $this->endWidget(); ?>            </div><!-- form -->        </div>    </div></div>   