<?php$this->pageTitle = Yii::app()->name . ' - ' . UserModule::t("Registration");$this->breadcrumbs = array(    UserModule::t("Registration"),);?><div class="row">    <div class="col-span-8" style="text-align:center">	<h2 class="big">Accelerate Your Business</h2>    </div>    <div class="col-span-4 registration">        <div class="" style="text-align:center">	    <?php if (Yii::app()->user->hasFlash('registration')): ?>    	    <div class="success">		    <?php //echo Yii::app()->user->getFlash('registration'); ?>		    <?php		    $this->widget('bootstrap.widgets.TbAlert', array(			'block' => true, // display a larger alert block?			'fade' => true, // use transitions?			'closeText' => '&times;', // close link text - if set to false, no close link is displayed			'alerts' => array(// configurations per alert type			    'registration' => array('block' => true, 'fade' => true, 'closeText' => '&times;'), // success, info, warning, error or danger			),		    ));		    ?>    	    </div>	    <?php else: ?>    	    <div class="form">		    <?php		    $form = $this->beginWidget('bootstrap.widgets.TbActiveForm', array(			'id' => 'registration-form',			'layout' => TbHtml::FORM_LAYOUT_HORIZONTAL,			'enableAjaxValidation' => true,//                        'disableAjaxValidationAttributes' => array('RegistrationForm_verifyCode'),			'htmlOptions' => array('enctype' => 'multipart/form-data'),		    ));		    ?>    		<div style="text-align:center"><h3># 会员注册 #</h3></div>    		<p class="help-block" style="text-align:center;padding-bottom:10px">请输入您的注册信息：</p>		    <?php //echo $form->errorSummary(array($model, $profile)); ?>		    <?php echo $form->textFieldControlGroup($model, 'username'); ?>		    <?php echo $form->passwordFieldControlGroup($model, 'password', array('hint' => UserModule::t("Minimal password length 4 symbols."))); ?>		    <?php echo $form->passwordFieldControlGroup($model, 'verifyPassword'); ?>		    <?php echo $form->textFieldControlGroup($model, 'email'); ?>		    <?php		    $profileFields = $profile->getFields();		    if ($profileFields) {			foreach ($profileFields as $field) {			    if ($field->widgetEdit($profile)) {				echo $field->widgetEdit($profile);			    } elseif ($field->range) {				echo $form->dropDownListControlGroup($profile, $field->varname, Profile::range($field->range));			    } elseif ($field->field_type == "TEXT") {				echo$form->textAreaControlGroup($profile, $field->varname, array('rows' => 6, 'cols' => 50));			    } else {				echo $form->textFieldControlGroup($profile, $field->varname, array('size' => 60, 'maxlength' => (($field->field_size) ? $field->field_size : 255)));			    }			}		    }		    ?>		    <?php if (UserModule::doCaptcha('registration')): ?>			<?php $this->widget('CCaptcha'); ?>			<?php echo $form->textFieldControlGroup($model, 'verifyCode', array('hint' => UserModule::t("Please enter the letters as they are shown in the image above.") . UserModule::t("Letters are not case-sensitive."))); ?>		    <?php endif; ?>				    <?php		    echo TbHtml::formActions(array(			TbHtml::submitButton('同意以下协议并注册', array('color' => TbHtml::BUTTON_COLOR_PRIMARY)),			TbHtml::resetButton('重置'),		    ));		    ?>    		<div class="control-group">    		    <div class="controls"><?php echo CHtml::link('[小商品分销网注册协议]', array('/page/index', 'key' => 'license'), array('style'=>'color:#5999a8')) ?></div></div>		<?php $this->endWidget(); ?>    	    </div><!-- form --><?php endif; ?>        </div>    </div></div>